settings.custom_liquid:
{% comment %} 
  Custom Elfsight Styles 
{% endcomment %}

<style>

{% comment %} 
  Card bg and text colors 
{% endcomment %}

#Rich-text-{{ section.id }} .swiper-slide {
  background: RGBA(var(--color-text) / .9);
  color: RGB(var(--color-background));
}

{% comment %} 
  Dots color (non-active) 
{% endcomment %}

#Rich-text-{{ section.id }} .dLvfKD > div > div {
  background: RGBA(var(--color-text) / .5);
}

{% comment %} 
  Dots color (active) 
{% endcomment %}

#Rich-text-{{ section.id }} .dLvfKD > div > .fumhUG {
  background: RGB(var(--color-text));
}

{% comment %} 
  Arrow container (both)
{% endcomment %}

#Rich-text-{{ section.id }} .iZcgmv, #Rich-text-{{ section.id }} .ckyeTq {
  display: block;
  margin: 0 !important;
  transform: 
  translateX(150%) !important;
}

{% comment %} 
  Arrow container (previous)
{% endcomment %}

#Rich-text-{{ section.id }} .ckyeTq {
  transform: scaleX(-1) 
  translateX(150%) !important;
}

{% comment %} 
  Elfsight's arrow
{% endcomment %}

#Rich-text-{{ section.id }} .ckyeTq > div {
  background: transparent !important;
  box-shadow: none !important;
  width: auto !important;
  height: auto !important;
}

{% comment %} 
  Elfsight's arrow's avg
{% endcomment %}

#Rich-text-{{ section.id }} .iZcgmv svg, #Rich-text-{{ section.id }} .ckyeTq svg {
  display: none;
}

{% comment %} 
  Add bolt arrow
{% endcomment %}

#Rich-text-{{ section.id }} .iZcgmv:has(div):after, #Rich-text-{{ section.id }} .ckyeTq:has(div):after {
  content: url('https://cdn.shopify.com/s/files/1/0271/7209/files/Path_224.svg?v=1672944245');
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  pointer-events: none;
}

</style>


{% comment %} 
  Elfsight Script
{% endcomment %}

<script src="https://static.elfsight.com/platform/platform.js" data-use-service-core="" defer="defer"></script>
<div class="elfsight-app-ee3f13dd-8870-40a3-a68b-ec618d1ac911"></div>
